import torchvision.transforms as transformsfrom torch.utils.data import DataLoaderfrom torchvision.datasets import MNIST# prepare train data and test datadef train_loader(batch_size):    train_data = MNIST('~/Project/deeplearning/mnist_data',            train=True, download=True,            transform=transforms.ToTensor())    train_loader = DataLoader(train_data, batch_size=batch_size,            shuffle=True)    return train_loader